package com.tempus.weijiujiao.bean;

public class User {
	private static User user;
	private String userName;
	private int userType;
	private String userID;
	private String userNumber;
	private String userEmail;
	private String userAddress;
	private String userRegisterTime;
	private String userImagerURL;
	private int gravidenCount=0;
	private int winecallerCount=0;
	private boolean isLogin=false;

	/**
	 * 单例用户模式
	 * 
	 * @return
	 */
	public static User getUser() {
		// TODO Auto-generated constructor stub
		if (user == null) {
			user = new User();
		}
		return user;
	}

	public String getUserName() {
		return userName;
	}

	public void setUserName(String userName) {
		this.userName = userName;
	}

	public String getUserID() {
		return userID;
	}

	public void setUserID(String userID) {
		this.userID = userID;
	}

	public int getUserType() {
		return userType;
	}

	public void setUserType(int userType) {
		this.userType = userType;
	}

	public String getUserNumber() {
		return userNumber;
	}

	public void setUserNumber(String userNumber) {
		this.userNumber = userNumber;
	}

	public String getUserEmail() {
		return userEmail;
	}

	public void setUserEmail(String userEmail) {
		this.userEmail = userEmail;
	}

	public String getUserAddress() {
		return userAddress;
	}

	public void setUserAddress(String userAddress) {
		this.userAddress = userAddress;
	}


	public String getUserImagerURL() {
		return userImagerURL;
	}

	public void setUserImagerURL(String userImagerURL) {
		this.userImagerURL = userImagerURL;
	}

	public boolean isLogin() {
		return isLogin;
	}

	public void setLogin(boolean isLogin) {
		this.isLogin = isLogin;
	}

	public String getUserRegisterTime() {
		return userRegisterTime;
	}

	public void setUserRegisterTime(String userRegisterTime) {
		this.userRegisterTime = userRegisterTime;
	}

	public int getGravidenCount() {
		return gravidenCount;
	}

	public void setGravidenCount(int gravidenCount) {
		this.gravidenCount = gravidenCount;
	}

	public int getWinecallerCount() {
		return winecallerCount;
	}

	public void setWinecallerCount(int winecallerCount) {
		this.winecallerCount = winecallerCount;
	}

}
